mapper.update:
UPDATE student
name = #{name}, no = #{no}, gender = #{gender}, phone = #{phone}, id_card = #{idCard}, is_college = #{isCollege}, address = #{address}, degree = #{degree}, graduation_date = #{graduationDate}, clazz_id = #{clazzId}, update_time = NOW()
WHERE id = #{id}

mapper.delete:
DELETE FROM student
WHERE id IN

    #{id}

mapper.select:
        select s.*, c.name clazz_name
        from student s left join clazz c on s.clazz_id=c.id
        
            
                AND s.name LIKE CONCAT('%', #{name}, '%')
            

            
                AND s.degree = #{degree}
            

            
                AND s.clazz_id = #{clazzId}
            
        
        ORDER BY s.update_time desc, s.id DESC
